def _explore_zipfile(zip_path):
    """
    Groups the given zip path by using _group_files_by_xml_filename.
    """
    import zipfile

    def _group_files_by_xml_filename(file_list):
        """
        Groups the given list of files by their XML filename.
        """
        xml_files = {}
        for file in file_list:
            filename = file.split('/')[-1]
            if filename.endswith('.xml'):
                xml_filename = filename.split('.xml')[0]
                if xml_filename not in xml_files:
                    xml_files[xml_filename] = []
                xml_files[xml_filename].append(file)
        return xml_files

    zip_file = zipfile.ZipFile(zip_path, 'r')
    file_list = zip_file.namelist()
    zip_file.close()

    return _group_files_by_xml_filename(file_list)

