    if style == "bold":
        for piece in pieces:
            result += "<b>" + piece + "</b>"
    elif style == "italic":
        for piece in pieces:
            result += "<i>" + piece + "</i>"
    elif style == "underline":
        for piece in pieces:
            result += "<u>" + piece + "</u>"
    elif style == "strikethrough":
        for piece in pieces:
            result += "<s>" + piece + "</s>"
    else:
        return "Invalid style"

    return result

