    return True

def validate_version_inventories(self, version_dirs):
    for version_dir in version_dirs:
        inventory_path = os.path.join(version_dir, "inventory.json")
        if not os.path.exists(inventory_path):
            raise ValueError(f"Inventory not found for version {version_dir}")

        with open(inventory_path, "r") as f:
            inventory = json.load(f)

        # Validate content digests in inventory
        for item in inventory:
            content_path = os.path.join(version_dir, item["path"])
            if not os.path.exists(content_path):
                raise ValueError(f"Content not found: {content_path}")

            content_digest = hashlib.sha256()
            with open(content_path, "rb") as f:
