def size_to_bytes(size: str) -> int:
    if size[-1].isalpha():
        num = int(size[:-1])
        unit = size[-1].lower()
        if unit == 'k':
            return num * 1024
        elif unit == 'm':
            return num * 1024 * 1024
        elif unit == 'g':
            return num * 1024 * 1024 * 1024
        elif unit == 't':
            return num * 1024 * 1024 * 1024 * 1024
        elif unit == 'p':
            return num * 1024 * 1024 * 1024 * 1024 * 1024
    return int(size)

