import datetime

def parse_frequency(frequency):
    if frequency is None or frequency.lower() == "always":
        return None

    parts = frequency.split()
    if len(parts) != 2:
        raise ValueError("Invalid frequency format")

    try:
        number = int(parts[0])
    except ValueError:
        raise ValueError("Invalid number in frequency")

    timeunit = parts[1].lower()
    if timeunit not in ["days", "hours", "minutes", "seconds", "microseconds", "milliseconds", "weeks"]:
        raise ValueError("Invalid timeunit in frequency")

    kwargs = {timeunit: number}
    return datetime.timedelta(**kwargs)

