import subprocess

def run_command(commands, args, cwd=None, verbose=False, hide_stderr=False, env=None):
    result = None
    try:
        if isinstance(commands, str):
            commands = [commands]
        cmd = commands + args
        if verbose:
            print(f"Running command: {' '.join(cmd)}")
        result = subprocess.run(cmd, cwd=cwd, capture_output=True, text=True, env=env)
        if hide_stderr:
            result.stderr = None
    except Exception as e:
        print(f"Error occurred while running command: {e}")
    return result.stdout, result.returncode

import subprocess

def run_command(commands, args, cwd=None, verbose=False, hide_stderr=False, env=None):
    process = subprocess.Popen([commands] + args, cwd=cwd, stdout=subprocess.PIPE, stderr=subprocess.PIPE, env=env)
    stdout, stderr = process.communicate()
    if not hide_stderr:
        stderr = stderr.decode('utf-8')
        if stderr:
            print(stderr)
    return stdout.decode('utf-8'), process.returncode

import subprocess

def run_command(commands, args, cwd=None, verbose=False, hide_stderr=False, env=None):
    result = {}
    result['stdout'] = ''
    result['returncode'] = None

    try:
        process = subprocess.Popen(commands + args, cwd=cwd, stdout=subprocess.PIPE, stderr=subprocess.PIPE, env=env)
        output, error = process.communicate()

        if not hide_stderr and error:
            result['stdout'] = error.decode()
        else:
            result['stdout'] = output.decode()

        result['returncode'] = process.returncode

        if verbose:
            print(f"Command: {' '.join(commands + args)}")
            print(f"Output: {result['stdout']}")
            print(f"Return Code: {result['returncode']}")

    except Exception as e:
        result['stdout'] = str(e)
        result['returncode'] = -1

        if verbose:
            print(f"Error: {result['stdout']}")
            print(f"Return Code: {result['returncode']}")

    return result

