from typing import Optional, Set
from rdflib import Graph, URIRef

def find_roots(
    graph: Graph, prop: URIRef, roots: Optional[Set] = None
) -> Set:
    """
    Find the roots in some sort of transitive hierarchy.
    """
    if roots is None:
        roots = set()

    for child, _, parent in graph.triples((None, prop, None)):
        roots.add(child)

    for child in roots.copy():
        for _, _, parent in graph.triples((None, prop, child)):
            roots.discard(parent)

    return roots

