def load_configurations(config_filenames, overrides=None, resolve_env=True):
    config_dict = {}
    error_logs = []

    for filename in config_filenames:
        try:
            with open(filename, 'r') as file:
                config_data = parse_config(file)
                config_dict[filename] = config_data
        except (IOError, OSError) as e:
            error_logs.append(logging.LogRecord(level=logging.ERROR, msg=str(e)))
        except Exception as e:
            error_logs.append(logging.LogRecord(level=logging.ERROR, msg=f"Error parsing configuration file {filename}: {str(e)}"))

    return config_dict, error_logs

from typing import List, Dict, Optional, Tuple
import logging

def load_configurations(config_filenames: List[str], overrides: Optional[Dict[str, str]] = None, resolve_env: bool = True) -> Tuple[Dict[str, dict], List[logging.LogRecord]]:
    parsed_configs = {}
    parse_errors = []

    for filename in config_filenames:
        try:
            with open(filename, 'r') as config_file:
                config_data = config_file.read()
                parsed_config = parse_config(config_data, overrides, resolve_env)
                parsed_configs[filename] = parsed_config
        except (IOError, OSError) as e:
            error_msg = f"Error reading configuration file '{filename}': {str(e)}"
            logging.error(error_msg)
            parse_errors.append(logging.LogRecord(level=logging.ERROR, msg=error_msg, pathname='', lineno=0, args=(), exc_info=None))
        except Exception as e:
            error_msg = f"Error parsing configuration file '{filename}': {str(e)}"
