def _verify(iface, candidate, tentative=False, vtype=None):
    if not tentative:
        if not iface.providedBy(candidate):
            raise zope.interface.Invalid("Candidate does not provide the interface")
    errors = []
    for name, method in iface.namesAndDescriptions():
        if not hasattr(candidate, name):
            errors.append(f"Missing method: {name}")
        elif not callable(getattr(candidate, name)):
            errors.append(f"Invalid method: {name}")
        else:
            sig = inspect.signature(getattr(candidate, name))
            if sig != method.getSignature():
                errors.append(f"Invalid method signature: {name}")
    for name, attr in iface.getAttributes():
        if not hasattr(candidate, name):
            errors.append(f"Missing attribute: {name}")
    if errors:
        if len(errors) == 1:
            raise zope.interface.Invalid(errors[0])
        else:
            raise zope.interface.Invalid(errors)
    return True

