    def parse_datetime(dt_str):
        dt_format = "%Y-%m-%dT%H:%M:%S"
        if "." in dt_str:
            dt_format += ".%f"
        return datetime.datetime.strptime(dt_str, dt_format)

    def parse_timezone(tz_str):
        if tz_str == "Z":
            return tz.tzutc()
        elif ":" in tz_str:
            hours, minutes = tz_str.split(":")
            return tz.tzoffset(None, int(hours) * 3600 + int(minutes) * 60)
        elif len(tz_str) == 3:
            hours = tz_str[0] + tz_str[1]
            return tz.tzoffset(None, int(hours) * 3600)

    if "T" in dt_str:
        date_str, time_str = dt_str.split("T")
        if "-" in date_str:
            if "-" in time_str:
                return parse_datetime(dt_str)
            else:
                return parse_iso_week(date_str)
        else:
            if ":" in time_str:
                return parse_datetime(dt_str)
            else:
                return parse_iso_week(date_str)
