from typing import Tuple, Set

def find_tags(text: str, replacer: callable = None) -> Tuple[Set, str]:
    if replacer is None:
        replacer = lambda x: x

    tags = set()
    replaced_text = text

    code_block_start = False
    tag_start = None

    for i in range(len(text)):
        if text[i:i+3] == "```":
            code_block_start = not code_block_start

        if not code_block_start and text[i] == "#":
            if tag_start is None:
                tag_start = i
            elif text[i-1].isspace():
                tag = text[tag_start+1:i].strip()
                tags.add(tag)

                if replacer:
                    replaced_text = replaced_text[:tag_start+1] + replacer(tag) + replaced_text[i:]

                tag_start = None

    return tags, replaced_text

