    if replacer is None:
        replacer = lambda x: x

    tags = set()
    code_block = False
    replaced_text = ""

    for char in text:
        if char == '`':
            code_block = not code_block

        if not code_block and char.isalpha():
            tag = char
            while True:
                char = text[text.index(char) + 1]
                if not char.isalpha():
                    break
                tag += char

            tags.add(tag)
            if replacer:
                replaced_text += replacer(tag)
            else:
                replaced_text += tag

        replaced_text += char

    return tags, replaced_text

