import requests
import xml.etree.ElementTree as ET

def retrieve_and_parse_diaspora_webfinger(handle):
    response = requests.get(f"https://{handle}/.well-known/webfinger")
    xml_data = response.content.decode("utf-8")
    root = ET.fromstring(xml_data)
    webfinger_data = {}
    for child in root.iter("{http://www.w3.org/2005/Atom}Link"):
        rel = child.attrib.get("rel")
        href = child.attrib.get("href")
        if rel and href:
            webfinger_data[rel] = href
    return webfinger_data

import requests
import xml.etree.ElementTree as ET

def retrieve_and_parse_diaspora_webfinger(handle):
    response = requests.get(f"https://{handle}/.well-known/webfinger")
    if response.status_code == 200:
        data = response.text
        root = ET.fromstring(data)
        webfinger_dict = {}
        for child in root.iter("{http://www.w3.org/2005/Atom}Link"):
            if child.attrib["rel"] == "http://microformats.org/profile/hcard":
                webfinger_dict["profile_url"] = child.attrib["href"]
            elif child.attrib["rel"] == "http://webfinger.net/rel/profile-page":
                webfinger_dict["diaspora_profile_url"] = child.attrib["href"]
        return webfinger_dict
    else:
        return {}

import requests
import xml.etree.ElementTree as ET

def retrieve_and_parse_diaspora_webfinger(handle):
    url = f"https://diaspora.example.com/.well-known/webfinger?resource=acct:{handle}"
    response = requests.get(url)
    xml_data = response.content

    webfinger_data = {}

    root = ET.fromstring(xml_data)
    for child in root.iter():
        if child.tag == "{http://docs.oasis-open.org/ns/xri/xrd-1.0}Alias":
            webfinger_data["alias"] = child.text
        elif child.tag == "{http://docs.oasis-open.org/ns/xri/xrd-1.0}Link":
