def _convert_non_cli_args(self, parser_name, values_dict):
    def convert_to_bool(value):
        if value.lower() in ['true', 't', 'yes', 'y', '1']:
            return True
        elif value.lower() in ['false', 'f', 'no', 'n', '0']:
            return False
        else:
            raise ValueError(f"Invalid boolean value: {value}")

    def convert_to_int(value):
        try:
            return int(value)
        except ValueError:
            raise ValueError(f"Invalid integer value: {value}")

    def convert_to_float(value):
        try:
            return float(value)
        except ValueError:
            raise ValueError(f"Invalid float value: {value}")

    def convert_to_list(value):
        return value.split(',')

    def convert_to_dict(value):
        try:
            return eval(value)
        except (NameError, SyntaxError):
            raise ValueError(f"Invalid dictionary value: {value}")

    def convert_to_tuple(value):
        try:
            return eval(value)
        except (NameError, SyntaxError):
            raise ValueError(f"Invalid tuple value: {value}")

    converters = {
        'bool': convert_to_bool,
        'int': convert_to_int,
        'float': convert_to_float,
        'list': convert_to_list,
        'dict': convert_to_dict,
        'tuple': convert_to_tuple
    }

    for key, value in values_dict.items():
        if isinstance(value, str):
            if value.startswith("'") and value.endswith("'"):
                value = value[1:-1]
            elif value.startswith('"') and value.endswith('"'):
                value = value[1:-1]
            else:
                value = value.strip()

            if parser_name in self._arg_types and key in self._arg_types[parser_name]:
                arg_type = self._arg_types[parser_name][key]
                if arg_type in converters:
                    try:
                        values_dict[key] = converters[arg_type](value)
                    except ValueError:
                        raise ValueError(f"Invalid value for argument '{key}': {value}")

    return values_dict

