            values_dict[key] = cast_to_bool(value)
        elif value.isdigit():
            values_dict[key] = cast_to_int(value)
        elif '.' in value and all(char.isdigit() for char in value.replace('.', '', 1)):
            values_dict[key] = cast_to_float(value)
        elif ',' in value:
            values_dict[key] = cast_to_list(value)
        elif ':' in value:
            values_dict[key] = cast_to_dict(value)
        else:
            values_dict[key] = value

